catalog_script_client.script:
// Type: onSubmit
// Applies to: このRecord Producer
function onSubmit() {
    var fieldName = 'work_start_tim0';

    var val = g_form.getValue(fieldName);
    if (!val) return true; // 未入力は別途カタログUIポリシー等で必須制御

    var input = new GlideDateTime(val); // 入力値（文字列でもOK）

    // 今月の範囲（onLoadと同条件）を再計算
    var start = new GlideDateTime();
    start.setDayOfMonth(1);
    start.setTime('00:00:00');

    var end = new GlideDateTime(start);
    end.addMonthsUTC(1);
    end.addSecondsUTC(-1);

    // 範囲判定（start <= input <= end）
    if (input.compareTo(start) < 0 || input.compareTo(end) > 0) {
        var msg = '勤務開始時間は「今月内（' + start.getDisplayValue() + ' ～ ' + end.getDisplayValue() + '）」のみ選択・入力可能です。';
        g_form.addErrorMessage(msg);
        return false; // 送信中止
    }

    return true; // 許可
}
