sys_variable_value.value:
(function execute(inputs, outputs) {
var num1 = parseFloat(inputs.work_time.toString());
  var num2 = parseFloat(inputs.count.toString());
  var ans = num1 + num2;
  outputs.ans = ans.toString();
})(inputs, outputs);

