sys_ui_action.script:
// current.status = '退勤';
// // current.description = 'カスタムボタンで更新';
// current.update();

// current.work_end_time = new GlideDateTime();
// current.status = '退勤';
// current.update();

(function executeAction() {
    // 退勤時間を現在時刻に設定
    // current.work_endtime = new GlideDateTime();

    // 勤務開始時間と退勤時間が両方ある場合のみ計算
    if (current.work_start_tim0 && current.work_endtime) {
        var startDate = new GlideDateTime(current.work_start_tim0);
        var endDate = new GlideDateTime(current.work_endtime);

        var diffMillis = endDate.getNumericValue() - startDate.getNumericValue();
        var hours = diffMillis / (1000 * 60 * 60);

        current.work_time = parseFloat(hours.toFixed(1));
    }

    // ステータス更新
    current.status = '退勤';
    current.description = 'カスタムボタンで更新';

    // レコード更新
    current.update();
})();

gs.info('退勤ボタン実行: sys_id=' + current.sys_id);
gs.info('勤務開始時間: sys_id=' + current.work_start_tim0);
gs.info('勤務開始時間: sys_id=' + current.work_endtime);
gs.info('勤務開始時間: sys_id=' + current.work_time);
